-- Now create mappings for the new drivers to stations
-- First, let's map US drivers to US stations
INSERT INTO station_fuel_friends (station_id, fuel_friend_id, is_active) 
SELECT 
    fs.id as station_id,
    ff.id as fuel_friend_id,
    true as is_active
FROM fuel_stations fs
CROSS JOIN fuel_friends ff
WHERE (fs.address LIKE '%WA%' AND ff.location LIKE '%Seattle%')
   OR (fs.address LIKE '%CA%' AND ff.location LIKE '%San Francisco%')
   OR (fs.address LIKE '%MA%' AND ff.location LIKE '%Boston%')
   OR (fs.address LIKE '%PA%' AND ff.location LIKE '%Philadelphia%')
   OR (fs.address LIKE '%AZ%' AND ff.location LIKE '%Phoenix%')
   OR (fs.address LIKE '%CO%' AND ff.location LIKE '%Denver%')
   OR (fs.address LIKE '%NV%' AND ff.location LIKE '%Las Vegas%')
   OR (fs.address LIKE '%GA%' AND ff.location LIKE '%Atlanta%')
   OR (fs.address LIKE '%OR%' AND ff.location LIKE '%Portland%')
   OR (fs.address LIKE '%MI%' AND ff.location LIKE '%Detroit%')
   OR (fs.address LIKE '%TX%' AND ff.location LIKE '%Austin%')
   OR (fs.address LIKE '%TN%' AND ff.location LIKE '%Nashville%')
   OR (fs.address LIKE '%NC%' AND ff.location LIKE '%Charlotte%')
   OR (fs.address LIKE '%MN%' AND ff.location LIKE '%Minneapolis%')
   OR (fs.address LIKE '%MO%' AND ff.location LIKE '%Kansas City%')
ON CONFLICT (station_id, fuel_friend_id) DO NOTHING;

-- Map UK drivers to UK stations  
INSERT INTO station_fuel_friends (station_id, fuel_friend_id, is_active)
SELECT 
    fs.id as station_id,
    ff.id as fuel_friend_id,
    true as is_active
FROM fuel_stations fs
CROSS JOIN fuel_friends ff
WHERE (fs.address LIKE '%Birmingham%' AND ff.location LIKE '%Birmingham%')
   OR (fs.address LIKE '%Leeds%' AND ff.location LIKE '%Leeds%')
   OR (fs.address LIKE '%Sheffield%' AND ff.location LIKE '%Sheffield%')
   OR (fs.address LIKE '%Newcastle%' AND ff.location LIKE '%Newcastle%')
   OR (fs.address LIKE '%Bristol%' AND ff.location LIKE '%Bristol%')
   OR (fs.address LIKE '%Nottingham%' AND ff.location LIKE '%Nottingham%')
   OR (fs.address LIKE '%Leicester%' AND ff.location LIKE '%Leicester%')
   OR (fs.address LIKE '%Cardiff%' AND ff.location LIKE '%Cardiff%')
   OR (fs.address LIKE '%Plymouth%' AND ff.location LIKE '%Plymouth%')
   OR (fs.address LIKE '%Southampton%' AND ff.location LIKE '%Southampton%')
   OR (fs.address LIKE '%Portsmouth%' AND ff.location LIKE '%Portsmouth%')
   OR (fs.address LIKE '%Brighton%' AND ff.location LIKE '%Brighton%')
   OR (fs.address LIKE '%Cambridge%' AND ff.location LIKE '%Cambridge%')
   OR (fs.address LIKE '%Oxford%' AND ff.location LIKE '%Oxford%')
   OR (fs.address LIKE '%Reading%' AND ff.location LIKE '%Reading%')
ON CONFLICT (station_id, fuel_friend_id) DO NOTHING;

-- Add some cross-regional assignments for better coverage
INSERT INTO station_fuel_friends (station_id, fuel_friend_id, is_active)
SELECT 
    fs.id as station_id,
    ff.id as fuel_friend_id,
    true as is_active
FROM fuel_stations fs
CROSS JOIN fuel_friends ff
WHERE fs.address LIKE '%NY%' AND ff.location LIKE '%New York%'
   OR fs.address LIKE '%CA%' AND ff.location LIKE '%Los Angeles%'
   OR fs.address LIKE '%London%' AND ff.location LIKE '%London%'
   OR fs.address LIKE '%Manchester%' AND ff.location LIKE '%Manchester%'
ON CONFLICT (station_id, fuel_friend_id) DO NOTHING;